import React,{useState} from 'react';
import './todoAdd.css'
const TodoAdd=()=>{
  const [title,setTitle]=useState("");
  const [descr,setDescr]=useState("");


  const handleTitleChanges=(event)=>{
    event.preventDefault();
    setTitle(event.target.value)

  };
  const handledescrChanges=(event)=>{
    event.preventDefault();
    setDescr(event.target.value)

  };
  const handleSUbmit=(event)=>{
    event.preventDefault();
    console.log({title,descr})
  }

  return(
    <div className='todoAdd-main-container'>
      <form action="" onSubmit={handleSUbmit}>

        <label htmlFor="title">Tile</label><br />
        <input type="text" placeholder='Add title' value={title} onChange={handleTitleChanges} /><br />
        <input type="text" placeholder='Add description' value={descr} onChange={handledescrChanges}/><br />
        <input type="submit" value="submit"/><br />
      </form>
    </div>
  )
}
export default TodoAdd
